    private void jBtIgualActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBtIgualActionPerformed
        secondNum=Double.parseDouble(jText.getText());
        String resultFinal;
        
       switch(operator)
        {
           case "+":
               result=firstNum+secondNum;
               resultFinal=Double.toString(result);
               jText.setText(resultFinal);
               break;
               
           case "-":
               result=firstNum-secondNum;
               resultFinal=Double.toString(result);
               jText.setText(resultFinal);
               break;
               
           case "/":
               result=firstNum/secondNum;
               resultFinal=Double.toString(result);
               jText.setText(resultFinal);
               break;
               
           case "*":
               result=firstNum*secondNum;
               resultFinal=Double.toString(result);
               jText.setText(resultFinal);
               break;
        }
    }//GEN-LAST:event_jBtIgualActionPerformed

